  const cartTotal = () => {
    // console.log("trying to get total");
    const total = cart.reduce((acc, item) => {
      const itemPrice = item.price * item.quantity;
      if (item.quantity >= 0) {
        return acc + itemPrice;
      }

      return acc;
    }, 0);
    // console.log(total);

    return total;
  };

  // VALIDATION ##################
  function CheckoutForm() {
    const [formData, setFormData] = useState({
      name: "",
      email: "",
      card: "",
    });

    const [validationErrors, setValidationErrors] = useState({});

    const handleChange = (e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
      setValidationErrors({ ...validationErrors, [name]: "" });
    };
    const handleSubmit = (e) => {
      e.preventDefault();

      // Validate the form data
      const errors = {};
      if (!formData.email.match(/^[^\s@]+@[^\s@]+\.[^\s@]+$/)) {
        errors.email = "Invalid email address";
      }
      if (formData.name.trim() === "") {
        errors.name = "Name is required";
      }
      if (!formData.card.match(/^[0-9]{4}-[0-9]{4}-[0-9]{4}-[0-9]{4}$/)) {
        errors.card = "Invalid card format";
      }

      if (Object.keys(errors).length === 0) {
        // Form is valid, you can proceed with submission
        alert("Form is valid and can be submitted.");
      } else {
        // Form is not valid, display errors
        setValidationErrors(errors);
      }
    };

    return (
      <div>
        <div class="container">
          <div class="row">
            <div class="col-2"></div>

            <div class="col-8">
              <h1 className="text-3xl category-title">Payment Information</h1>

              <div id="liveAlertPlaceholder"></div>
              <form class="row g-3" id="checkout-form">
                <div class="col-md-6">
                  <label for="inputName" class="form-label">
                    Full Name
                  </label>
                  <input
                    type="text"
                    className={`form-control ${
                      validationErrors.name ? "is-invalid" : ""
                    }`}
                    id="inputName"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                  />
                  <div class="valid-feedback">Looks good!</div>
                  <div class="invalid-feedback">Must be like, "John Doe"</div>
                </div>
                <button type="submit" className="">
                  <i className=""></i> Order
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // CONFIRMATION CODE ################

  const showProducts = (ProductsCategory) => {
    return (
      <div>
        <h2 className="text-3xl font-extrabold tracking-tight text-gray-600 category-title">
          Products ({ProductsCategory.length})
        </h2>
        <div
          className="m-6 p-3 mt-10 ml-0 grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-6 xl:gap-x-10"
          style={{ maxHeight: "800px", overflowY: "scroll" }}
        ></div>

        <div className="container">
          <div className="row row-cols-1 row-cols-md-3 g-3">
            {ProductsCategory.map((product, index) => (
              <div className="col" key={index}>
                <div className="card shadow-sm">
                  <img alt="Product Image" src={product.image} />
                  <div className="card-body">
                    <span style={{ fontSize: "16px", fontWeight: "600" }}>
                      {product.title}
                    </span>
                    <p className="card-text">
                      {product.price} <br></br>
                      <strong>Description: </strong>
                      {product.description}
                      <br></br>
                      <strong>Tag</strong> - {product.category}
                    </p>
                    <div className="d-flex justify-content-between align-items-center">
                      <button
                        onClick={() => {
                          addToCart(product);
                        }}
                        className="btn btn-sm btn-primary"
                      >
                        {" "}
                        +{" "}
                      </button>
                      <span className="item-quantity">
                        Quantity:{" "}
                        {cart.find((item) => item.id === product.id)
                          ?.quantity ?? 0}
                      </span>
                      <button
                        onClick={() => {
                          removeFromCart(product);
                        }}
                        className="btn btn-sm btn-primary"
                      >
                        {" "}
                        -{" "}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div>
      <div className="top-0 p-4 flex justify-between items-center">
        <h1 className="text-3xl">
          <strong>IASG Online Store</strong>{" "}
        </h1>
        <button
          className="btn btn-md btn-primary"
          onClick={() => toggleViews()}
        >
          {isBrowseViewVisible ? "Checkout" : "Return"}
        </button>
      </div>
      <div
        className="browseView"
        style={{ display: isBrowseViewVisible ? "block" : "none" }}
      >
        <div>
          <div>
            <div>
              <div className="py-10">
                <input
                  id="search"
                  className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg
focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700
