import "./App.css";
// import logo from "./logo.png";
import React, { useState, useEffect } from "react";
import { Products } from "./Products";

const App = () => {
  function toggleViews() {
    setIsBrowseViewVisible(!isBrowseViewVisible);
    setIsCartViewVisible(!isCartViewVisible);
  }

  const [isBrowseViewVisible, setIsBrowseViewVisible] = useState(true);
  const [isCartViewVisible, setIsCartViewVisible] = useState(false);

  // BROWSE CODE ################
  const [ProductsCategory, setProductsCategory] = useState(Products);

  const [query, setQuery] = useState("");

  const handleChange = (e) => {
    setQuery(e.target.value);
    const results = Products.filter((eachProduct) => {
      if (e.target.value === "") return ProductsCategory;
      //   console.log(eachProduct);
      //   console.log(e.target.value.toLowerCase());
      return eachProduct.title
        .toLowerCase()
        .includes(e.target.value.toLowerCase());
    });
    setProductsCategory(results);
  };

  // CART CODE ################
  const [cart, setCart] = useState([]);

  const getQuan = (item) => {
    return item.quantity;
  };

  const addToCart = (item) => {
    console.log(item);
    const existingCart = cart.find((cartItem) => cartItem.id === item.id);

    if (existingCart) {
      const updatedCart = cart.map((cartItem) => {
        if (cartItem.id === item.id) {
          return { ...cartItem, quantity: cartItem.quantity + 1 };
        }
        return cartItem;
      });

      setCart(updatedCart);
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }
    console.log(cart);
  };

  const removeFromCart = (el) => {
    const updatedCart = cart.map((cartItem) => {
      if (cartItem.id === el.id && cartItem.quantity > 0) {
        return { ...cartItem, quantity: cartItem.quantity - 1 };
      }
      return cartItem;
    });

    setCart(updatedCart);
  };

  const cartItems = cart
    .filter((item) => item.quantity >= 1)
    .map((item) => (
      <div key={item.id}>
        <img className="img-fluid" src={item.image} width={150} />
        {item.title}
        <br></br>${item.price}
        <br></br>
        {item.quantity}
      </div>
    ));

	const cartTotal = () => {
		const total = cart.reduce((acc, item) => {
		  const itemPrice = item.price * item.quantity;

		  if (item.quantity >= 0) {
			return acc + itemPrice;
		  }

		  return acc;
		}, 0);

		return total;
	  };


  // CONFIRMATION CODE ################

  const showProducts = (ProductsCategory) => {
    return (
      <div>
        <h2 className="text-3xl font-extrabold tracking-tight text-gray-600 category-title">
          Products ({ProductsCategory.length})
        </h2>
        <div
          className="m-6 p-3 mt-10 ml-0 grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-6 xl:gap-x-10"
          style={{ maxHeight: "800px", overflowY: "scroll" }}
        ></div>

        <div className="container">
          <div className="row row-cols-1 row-cols-md-3 g-3">
            {ProductsCategory.map((product, index) => (
              <div className="col" key={index}>
                <div className="card shadow-sm">
                  <img alt="Product Image" src={product.image} />
                  <div className="card-body">
                    <span style={{ fontSize: "16px", fontWeight: "600" }}>
                      {product.title}
                    </span>
                    <p className="card-text">
                      {product.price} <br></br>
                      <strong>Description: </strong>
                      {product.description}
                      <br></br>
                      <strong>Tag</strong> - {product.category}
                    </p>
                    <div className="d-flex justify-content-between align-items-center">
                      <button
                        onClick={() => {
                          addToCart(product);
                        }}
                        className="btn btn-sm btn-primary"
                      >
                        {" "}
                        +{" "}
                      </button>
                      <span className="item-quantity">
                        Quantity:{" "}
                        {cart.find((item) => item.id === product.id)
                          ?.quantity ?? 0}
                      </span>
                      <button
                        onClick={() => {
                          removeFromCart(product);
                        }}
                        className="btn btn-sm btn-primary"
                      >
                        {" "}
                        -{" "}
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div>
      <button className="btn btn-md btn-primary" onClick={() => toggleViews()}>
        Show Cart
      </button>
      <div
        className="browseView"
        style={{ display: isBrowseViewVisible ? "block" : "none" }}
      >
        <div>
          <div>
            <div>
              <h1> IASG Online Store </h1>
              <div className="py-10">
                <input
                  className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg
focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700
dark:border-gray-600 dark:placeholder-gray-400 dark:text-white
dark:focus:ring-blue-500 dark:focus:border-blue-500"
                  type="search"
                  value={query}
                  onChange={handleChange}
                />
              </div>
            </div>
          </div>
          <div>{showProducts(ProductsCategory)}</div>
        </div>
      </div>

      <div
        className="cartView"
        style={{ display: isCartViewVisible ? "block" : "none" }}
      >
        <h1>This is a cart</h1>
        <div>Itesm in Cart :</div>
        <div>{cartItems}</div>

        <div>
          <div class="card">
            <div class="row">
              <div class="col-md-8 cart">
                <div class="title">
                  <div class="row">
                    <div class="col">
                      <h4>
                        <b>319 Shopping Cart</b>
                      </h4>
                    </div>
                    <div class="col align-self-center text-right text-muted">
                      Products selected {cart.length}
                    </div>
                  </div>
                </div>
                <div>{listItems}</div>
              </div>
              <div class="float-end">
                <p class="mb-0 me-5 d-flex align-items-center">
                  <span class="small text-muted me-2">Order total:</span>
                  <span class="lead fw-normal">${cartTotal}</span>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="confirmationView"></div>
    </div>
  );
};

export default App;
