import "./App.css";
// import logo from "./logo.png";
import React, { useState, useEffect } from "react";
import { Products } from "./Products";

const App = () => {
  const [ProductsCategory, setProductsCategory] = useState(Products);

  const [query, setQuery] = useState("");

  function toggleViews() {
    setIsBrowseViewVisible(!isBrowseViewVisible);
    setIsCartViewVisible(!isCartViewVisible);

    const searchInput = document.getElementById("search");
    console.log(searchInput.value);
    searchInput.value = "";
    setQuery("");

	handleChange({ target: { value: "" } });
  }

  const [isBrowseViewVisible, setIsBrowseViewVisible] = useState(true);
  const [isCartViewVisible, setIsCartViewVisible] = useState(false);

  // BROWSE CODE ################

  const handleChange = (e) => {
    setQuery(e.target.value);
    const results = Products.filter((eachProduct) => {
      if (e.target.value === "") return ProductsCategory;
      //   console.log(eachProduct);
      //   console.log(e.target.value.toLowerCase());
      return eachProduct.title
        .toLowerCase()
        .includes(e.target.value.toLowerCase());
    });
    setProductsCategory(results);
  };

  // CART CODE ################
  const [cart, setCart] = useState([]);

  const addToCart = (item) => {
    // console.log(item);
    const existingCart = cart.find((cartItem) => cartItem.id === item.id);

    if (existingCart) {
      const updatedCart = cart.map((cartItem) => {
        if (cartItem.id === item.id) {
          return { ...cartItem, quantity: cartItem.quantity + 1 };
        }
        return cartItem;
      });

      setCart(updatedCart);
    } else {
      setCart([...cart, { ...item, quantity: 1 }]);
    }
    // console.log(cart);
  };

  const removeFromCart = (item) => {
    const updatedCart = cart.map((cartItem) => {
      if (cartItem.id === item.id && cartItem.quantity > 0) {
        return { ...cartItem, quantity: cartItem.quantity - 1 };
      }
      return cartItem;
    });

    setCart(updatedCart);
  };

  const cartItems = cart
    .filter((item) => item.quantity >= 1)
    .map((item) => (
      <div key={item.id}>
        <div className="row border-top border-bottom" key={item.id}>
          <div className="row main align-items-center">
            <div className="col-2">
              <img className="img-fluid" src={item.image} />
            </div>
            <div className="col">
              <div className="row text-muted">{item.title}</div>
              <div className="row">{item.category}</div>
            </div>
            <div className="col"></div>
            <div className="col">${item.price}</div>
            <div className="col">Quantity: {item.quantity}</div>
          </div>
        </div>
      </div>
    ));

  const cartTotal = () => {
    // console.log("trying to get total");
    const total = cart.reduce((acc, item) => {
      const itemPrice = item.price * item.quantity;
      if (item.quantity >= 0) {
        return acc + itemPrice;
      }

      return acc;
    }, 0);
    // console.log(total);

    return total;
  };


  // VALIDATION ##################
  const alertPlaceholder = document.getElementById("liveAlertPlaceholder");
const form = document.getElementById("checkout-form");
const inputCard = document.querySelector("#inputCard");
const alertTrigger = document.getElementById("submit-btn");
const summaryCard = document.querySelector(".card");
const summaryList = document.querySelector(".card > ul");
console.log(summaryList);

const alert = (message, type) => {
  const wrapper = document.createElement("div");
  wrapper.innerHTML = [
    `<div class="alert alert-${type} alert-dismissible" role="alert">`,
    ` <div>${message}</div>`,
    ' <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>',
    "</div>",
  ].join("");
  alertPlaceholder.append(wrapper);
};

var order = { name: "", email: "", card: "" };

function isNumeric(n) {
  return !isNaN(parseFloat(n)) && isFinite(n);
}

inputCard.addEventListener("input", (event) => {
  if (!inputCard.value) {
    return event.preventDefault(); // stops modal from being shown
  } else {
    inputCard.value = inputCard.value.replace(/-/g, "");
    let newVal = "";
    for (var i = 0, nums = 0; i < inputCard.value.length; i++) {
      if (nums != 0 && nums % 4 == 0) {
        newVal += "-";
      }
      newVal += inputCard.value[i];
      if (isNumeric(inputCard.value[i])) {
        nums++;
      }
    }
    inputCard.value = newVal;
  }
});

